mapper.select:
SELECT DATE_FORMAT(date,'%Y') AS year
FROM attendance_day
WHERE 1 = 1

  AND employee_id = #{params.employeeId}


  AND project_id = #{params.projectId}

GROUP BY DATE_FORMAT(date,'%Y')

SELECT DATE_FORMAT(date,'%Y') AS year,DATE_FORMAT(date,'%m') AS month,CAST(SUM(forenoon + afternoon) / 2 AS DECIMAL(10,1)) AS day,SUM(hour) AS hour
FROM attendance_day
WHERE 1 = 1

  AND DATE_FORMAT(date,'%Y') = #{params.year}


  AND employee_id = #{params.employeeId}


  AND project_id = #{params.projectId}

GROUP BY DATE_FORMAT(date,'%m')

SELECT CAST(IFNULL((SUM(forenoon) + SUM(afternoon)) / 2,0)  AS DECIMAL(10,1)) AS day, IFNULL(SUM(hour),0) AS hour
FROM attendance_day
WHERE employee_id = #{params.employeeId}
AND project_id = #{params.projectId}
AND DATE_FORMAT(date,'%Y') = #{params.year}
AND DATE_FORMAT(date,'%m') = #{params.month}
GROUP BY DATE_FORMAT(date,'%m')
