mapper.select:
SELECT e.id AS employeeId,e.name AS employeeName,e.type AS workType,p.id AS projectId,p.name AS projectName, DATE_FORMAT(p.start_date,'%Y-%m-%d') AS startDate,DATE_FORMAT(p.end_date,'%Y-%m-%d') AS endDate, p.no AS projectNo,CAST(CONVERT(p.total_price,DECIMAL(15,2)) AS CHAR) AS totalPrice,c.name AS companyName FROM employee e LEFT JOIN project_employee pe ON pe.employee_id = e.id LEFT JOIN project p ON p.id = pe.project_id LEFT JOIN company c ON c.id = p.company_id WHERE e.delete_mark = 0
AND e.id = #{params.employeeId}
ORDER BY pe.date_insert DESC
